boann.config(['$stateProvider', '$urlMatcherFactoryProvider', '$urlRouterProvider', '$locationProvider', 
    function($stateProvider, $urlMatcherFactoryProvider, $urlRouterProvider, $locationProvider){
        $urlRouterProvider.otherwise("/");
        $urlMatcherFactoryProvider.caseInsensitive(true);
        
        $stateProvider
            .state({
                name:"login",
                url: "/",
                views : {
                    "loginPage" : {
                        templateUrl : "./html/login/login.html?v="+controler.version,                        
                        controller: "LoginController",
                    },
                }
            })
            .state({
                name:"register",
                url: "/register/",
                views : {
                    "loginPage" : {
                        templateUrl : "./html/login/register.html?v="+controler.version,                        
                        controller: "RegisterController",
                    },
                }
            })

            .state({
                name:"wachtwoord_vergeten",
                url: "/wachtwoord_vergeten/",
                views : {
                    "loginPage" : {
                        templateUrl : "./html/login/forget_password.html?v="+controler.version,                        
                        //controller: "LoginController",
                    },
                }
            });        
}]);